// Series1 annotations ChartSeriesAnnotation ser1LowPoint = new ChartSeriesAnnotation() { X = 1, Y = 20, Description = "Series 1 Low Point" }; ChartSeriesAnnotation ser1HighPoint = new ChartSeriesAnnotation() { X = 7, Y = 56, Description = "Series 1 High Point" }; this.Chart1.Areas[0].Series[0].Annotations.Items.Add(ser1LowPoint); this.Chart1.Areas[0].Series[0].Annotations.Items.Add(ser1HighPoint);

