<syncfusion:ChartArea>
<syncfusion:ChartSeries Data="1 1 2 2 3 3 4 4"/>
<syncfusion:ChartArea.PrimaryAxis>
<!--Setting small tick size-->
<syncfusion:ChartAxis SmallTickSize="15"/>
</syncfusion:ChartArea.PrimaryAxis>
